package ch02;

public class 영어_소문자를_대문자로 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Java에서는 문자열을 대문자 혹은 소문자로 변환하고 싶다면 String클래스의 toUpperCase()와
		//toLowerCase를 사용하면 된다. 또한 equalslgnoreCase()를 사용하여 대소문자 구분없이 문자열을
		//비교할 수 있다.
		
		// toUpperCase(): 소문자 -> 대문자
		String word="a";
		word=word.toUpperCase();
		System.out.println(word);
		// toLowerCase(): 대문자 -> 소문자
	
		
	
	
	
	
	
	
	
	}
	

}
